<?php	// include Database connection file 	require_once '../common/header-session.php';	require_once '../common/connect.php';	$dataSostituzione = $_GET["data"];	$limit = $_GET["limit"];	$offset = $_GET["offset"];	// Design initial table header 	$data = '';	$query = "	SELECT					sostituzione.id AS sostituzione_id,					docente_incaricato.nome AS docente_incaricato_nome,					docente_incaricato.cognome AS docente_incaricato_cognome,					docente_assente.nome AS docente_assente_nome,					docente_assente.cognome AS docente_assente_cognome,					classe.nome AS classe_nome,					ora_insegnamento.orario as ora_insegnamento_orario,					ora_insegnamento.tipo as ora_insegnamento_tipo,					aula.codice as aula_codice,					aula.nome_diurno as aula_nome_diurno,					aula.nome_serale_eda as aula_nome_serale_eda,					aula.piano as piano,					tipo_sostituzione.codice as tipo_sostituzione_codice,					tipo_sostituzione.colore as tipo_sostituzione_colore				FROM sostituzione				INNER JOIN docente docente_incaricato				ON sostituzione.docente_incaricato_id = docente_incaricato.id				INNER JOIN docente docente_assente				ON sostituzione.docente_assente_id = docente_assente.id				INNER JOIN classe classe				ON sostituzione.classe_id = classe.id				INNER JOIN ora_insegnamento ora_insegnamento				ON sostituzione.ora_insegnamento_id = ora_insegnamento.id				INNER JOIN aula aula				ON sostituzione.aula_id = aula.id				INNER JOIN tipo_sostituzione tipo_sostituzione				ON sostituzione.tipo_sostituzione_id = tipo_sostituzione.id				WHERE					sostituzione.anno_scolastico_id = $__anno_scolastico_corrente_id				AND					sostituzione.data = '".$dataSostituzione."'					";	$query .= "				order by docente_incaricato.cognome ASC, docente_incaricato.nome ASC ";	$query .= " 				LIMIT ".$limit."				OFFSET ".$offset."					";//debug($query);//info($query);	if (!$result = mysqli_query($con, $query)) {		exit(mysqli_error($con));	}	// if query results contains rows then fetch those rows 	if(mysqli_num_rows($result) > 0) {		while($row = mysqli_fetch_assoc($result)) {//			console_log_data("docente=", $row);			$tipoMarker = '<span class=\'label label-info\'						style=\'background-color: '.$row['tipo_sostituzione_colore'].';\'						>'.$row['tipo_sostituzione_codice'].'</span>';			$data .= '<tr>			<td>'.$row['docente_incaricato_cognome'].' '.$row['docente_incaricato_nome'].'</td>			<td>'.$row['ora_insegnamento_orario'].'</td>			<td>'.$row['classe_nome'].'</td>			<td>'.$row['aula_nome_diurno'].'</td>			<td>'.$row['docente_assente_cognome'].' '.$row['docente_assente_nome'].'</td>			<td>'.$tipoMarker.'</td>			</tr>			';		}    }info($data);    echo $data;?>